(function(){var each=tinymce.each;tinymce.PluginManager.requireLangPack('moodlemedia');tinymce.create('tinymce.plugins.MoodlemediaPlugin',{init:function(ed,url){var t=this;t.editor=ed;t.url=url;ed.addCommand('mceMoodleMedia',function(){ed.windowManager.open({file:url+'/moodlemedia.htm',width:480+parseInt(ed.getLang('media.delta_width',0)),height:480+parseInt(ed.getLang('media.delta_height',0)),inline:1},{plugin_url:url})});ed.addButton('moodlemedia',{title:'moodlemedia.desc',image:url+'/img/icon.gif',cmd:'mceMoodleMedia'})},_parse:function(s){return tinymce.util.JSON.parse('{'+s+'}')},getInfo:function(){return{longname:'Moodle media',author:'Dongsheng Cai <dongsheng@moodle.com>',version:"1.0"}}});tinymce.PluginManager.add('moodlemedia',tinymce.plugins.MoodlemediaPlugin)})()